import { Checkbox, CheckboxControl, CheckboxLabel, type CheckedState } from '@dominiclisjak/react'
import { useState } from 'react'

const Controlled = () => {
  const [checked, setChecked] = useState<CheckedState>(true)
  return (
    <>
      <Checkbox checked={checked} onCheckedChange={(e) => setChecked(e.checked)}>
        <CheckboxLabel>Checkbox</CheckboxLabel>
        <CheckboxControl />
      </Checkbox>
    </>
  )
}

